class Hexagram{
  //周易上経三十卦・周易下経三十四卦での番号(assets/memo/about.txt参照)
  late int number;
  //バイナリ番号(assets/memo/about.txt参照)
  late String binaryNumber;
  //名前(例.乾為天)
  late String name;
  //名前かな(例.けんいてん)
  late String nameKana;
  //UniCode上での表示(例.䷀)
  late String shape;
  //キーワード
  late String keyWord;
  //卦辞
  late String originalOutline;
  late String meanOutline;
  //象伝
  late String originalSummary;
  late String meanSummary;

  late List<String> originalLines;
  late List<String> meanLines;
  //初爻
  late String firstLineOriginal;
  late String firstLineMean;
  //二爻
  late String secondLineOriginal;
  late String secondLineMean;
  //三爻
  late String thirdLineOriginal;
  late String thirdLineMean;
  //四爻
  late String fourthLineOriginal;
  late String fourthLineMean;
  //五爻
  late String fifthLineOriginal;
  late String fifthLineMean;
  //上爻
  late String finalLineOriginal;
  late String finalLineMean;
  //用爻
  late String anotherLineOriginal;
  late String anotherLineMean;

  Hexagram(
      int number, String binaryNumber, String name, String nameKana, String shape,
      String keyWord, String originalOutline, String meanOutline, String originalSummary, String meanSummary,
      String firstLineOriginal, String firstLineMean, String secondLineOriginal, String secondLineMean,
      String thirdLineOriginal, String thirdLineMean, String fourthLineOriginal, String fourthLineMean,
      String fifthLineOriginal, String fifthLineMean, String finalLineOriginal, String finalLineMean,
      String anotherLineOriginal, String anotherLineMean
      ){
          this.number = number;
          this.binaryNumber = binaryNumber;
          this.name = name;
          this.nameKana = nameKana;
          this.shape = shape;
          this.keyWord = keyWord;
          this.originalOutline = originalOutline;
          this.meanOutline = meanOutline;
          this.originalSummary = originalSummary;
          this.meanSummary = meanSummary;
          this.firstLineOriginal = firstLineOriginal;
          this.firstLineMean = firstLineMean;
          this.secondLineOriginal = secondLineOriginal;
          this.secondLineMean = secondLineMean;
          this.thirdLineOriginal = thirdLineOriginal;
          this.thirdLineMean = thirdLineMean;
          this.fourthLineOriginal = fourthLineOriginal;
          this.fourthLineMean = fourthLineMean;
          this.fifthLineOriginal = fifthLineOriginal;
          this.fifthLineMean = fifthLineMean;
          this.finalLineOriginal = finalLineOriginal;
          this.finalLineMean = finalLineMean;
          this.anotherLineOriginal = anotherLineOriginal;
          this.anotherLineMean = anotherLineMean;

          originalLines = [firstLineOriginal, secondLineOriginal, thirdLineOriginal, fourthLineOriginal, fifthLineOriginal, finalLineOriginal];
          meanLines = [firstLineMean, secondLineMean, thirdLineMean, fourthLineMean, fifthLineMean, finalLineMean];
  }
}

